// src/services/api.ts
import axios from 'axios';

// Configura o Axios para usar a URL base da API
const api = axios.create({
  baseURL: 'https://localhost:7275/api', // Altere para a URL do seu backend
});

// ID fixo do usuário para associar todas as tarefas
const USER_ID = 18;

// Funções CRUD para tarefas

// Buscar todas as tarefas associadas ao usuário com ID 18
export const fetchTasks = () => api.get(`/Tarefas/usuario/${USER_ID}`);

// Criar uma nova tarefa associada ao usuário com ID 18
export const createTask = (task: any) =>
  api.post('/Tarefas', { ...task, userId: USER_ID });

// Atualizar uma tarefa existente
export const updateTask = (id: string, task: any) =>
  api.put(`/Tarefas/${id}`, { ...task, userId: USER_ID });

// Deletar uma tarefa
export const deleteTask = (id: string) => api.delete(`/Tarefas/${id}`);

export default api;
