// src/components/TaskForm.tsx
import React, { useState } from 'react';

interface TaskFormProps {
  onSubmit: (task: any) => void;
  task?: any;
}

// Componente de formulário para adicionar ou editar uma tarefa
const TaskForm: React.FC<TaskFormProps> = ({ onSubmit, task }) => {
  const [title, setTitle] = useState(task ? task.title : '');
  const [description, setDescription] = useState(task ? task.description : '');

  // Função para lidar com o envio do formulário de tarefa
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit({ title, description });
    setTitle('');
    setDescription('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Título" required />
      <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Descrição" required />
      <button type="submit">{task ? 'Atualizar Tarefa' : 'Adicionar Tarefa'}</button>
    </form>
  );
};

export default TaskForm;
