import React from 'react';
import TaskList from '../components/TaskList';
import TaskForm from '../components/TaskForm';
import { useAuth } from '../hooks/useAuth'; // Importa o hook useAuth para acessar o contexto de autenticação

// Componente funcional para a página inicial
const HomePage: React.FC = () => {
  const { user } = useAuth(); // Usa o hook useAuth para obter o usuário autenticado

  // Verifica se há um usuário autenticado
  if (!user) {
    return <p>Por favor, faça login para ver suas tarefas.</p>;
  }

  return (
    <div>
      <h1>Gerenciador de Tarefas</h1>
      {/* Passa o userId (nome de usuário) para o componente TaskList */}
      <TaskList userId={user} /> 
      <TaskForm /> {/* Componente para adicionar/editar tarefas */}
    </div>
  );
};

export default HomePage;
