// src/components/TaskList.tsx
import React, { useEffect, useState } from 'react';
import { fetchTasks, createTask, updateTask, deleteTask } from '../services/api'; // Importa funções do api.ts
import TaskItem from './TaskItem';
import TaskForm from './TaskForm';

// Componente que gerencia a lista de tarefas
const TaskList: React.FC = () => {
  const [tasks, setTasks] = useState<any[]>([]);
  const [editingTask, setEditingTask] = useState<any | null>(null);

  // Busca todas as tarefas ao carregar o componente
  useEffect(() => {
    fetchTasks().then((response) => setTasks(response.data)); // Sem autenticação
  }, []);

  // Função para adicionar uma nova tarefa
  const handleAddTask = async (task: any) => {
    try {
      const response = await createTask(task);
      setTasks([...tasks, response.data]);
    } catch (error) {
      console.error('Falha ao criar tarefa', error);
    }
  };

  // Função para atualizar uma tarefa existente
  const handleUpdateTask = async (task: any) => {
    try {
      const response = await updateTask(task.id, task);
      setTasks(tasks.map((t) => (t.id === task.id ? response.data : t)));
      setEditingTask(null);
    } catch (error) {
      console.error('Falha ao atualizar tarefa', error);
    }
  };

  // Função para deletar uma tarefa
  const handleDeleteTask = async (id: string) => {
    try {
      await deleteTask(id);
      setTasks(tasks.filter((task) => task.id !== id));
    } catch (error) {
      console.error('Falha ao deletar tarefa', error);
    }
  };

  return (
    <div>
      <TaskForm onSubmit={editingTask ? handleUpdateTask : handleAddTask} task={editingTask} />
      {tasks.map((task) => (
        <TaskItem key={task.id} task={task} onDelete={handleDeleteTask} onEdit={setEditingTask} />
      ))}
    </div>
  );
};

export default TaskList;
